import { Link, useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import React from "react";

import noAvatar from "../files/icons8-avatar-100.png";

import api from '../modules/api';

import '../cssFiles/style_userpage.css'
import Card from "../components/Card.js";
import VideoCard from "../components/VideoCard.js";
import Header from "../components/Header/Header";

function UserPage() {
    const [pix, setPix] = useState([]);
    const [isHiddenPicture, setHiddenPicture] = useState("none");
    const [isHiddenCards, setHiddenCards] = useState("none");

    const { username, type } = useParams();
    const [currentUser, setCurrentUser] = useState({avatar: noAvatar, name: `${username}`});

    useEffect(() => {
        const loadImages = async (username) => {
            const data = await api.getUsersImages(username);
            if (data.total === 0) {
                setHiddenCards("none");
                setHiddenPicture("flex");
            }
            else {
                setPix(data.hits.map((item => <Card key={ item.id } item={ item } />)));
                getUserInfo(username, data.hits);
                setHiddenCards("flex");
                setHiddenPicture("none");
            }
        }

        const loadVideos = async (username) => {
            const data = await api.getUsersVideos(username);
            if (data.total === 0) {
                setHiddenCards("none");
                setHiddenPicture("flex");
            }
            else {
                setPix(data.hits.map((item => <VideoCard key={ item.id } item={ item } />)));
                getUserInfo(username, data.hits);
                setHiddenCards("flex");
                setHiddenPicture("none");
            }
        }

        if(type === 'images') {
            loadImages(username);
        } else if (type === "videos") {
            loadVideos(username);
        }
    }, [username, type]);


    const getUserInfo = (username, hits) => {
        if(hits.length === 0) return;
        setCurrentUser({
            avatar: hits[0]["userImageURL"],
            name: username
        });
    }

    return (
        <div>
            <Header />
            <main className="main_container">
                <div id="user-block">
                    <h2>User Profile:</h2>
                    <a href={`https://pixabay.com/ru/users/${ username }/`} style={{ textDecoration: 'none', color: '#584343'}} target="_blank">
                        <div className="userInfo">
                            <img className="userAvatar" src={currentUser.avatar} alt={ username } />
                            <p className="userName">{currentUser.name}</p>
                        </div>
                    </a>
                    <div className="userInfo"style={{ marginTop: '20px' }}>
                        <Link to={ `/user/${ username }/images` } className="link">Images</Link>
                        <Link to={ `/user/${ username }/videos` } className="link">Videos</Link>
                    </div>
                </div>
                <div className="user-img_container" style={{ display: isHiddenCards }}>
                    { pix }
                </div>
                <div className="pictureNotFound" style={{ display: isHiddenPicture }}>
                    <img src='data:image/png;base64,' alt="Not found"></img>
                </div>
            </main>
            
        </div>
    );
}


export default UserPage;